package labsessions._22_07;

public class SumFirst10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int sum = 0;
	        for (int i = 1; i <= 10; i++) {
	            sum += i;
	        }
	        System.out.println("Sum of first 10 natural numbers: " + sum);
	}

}
